form.grammar:

MainRule[
 ( TypeChoice CurrencyChoice CardChoice )
]

TypeChoice[
  [ normal ]  { <type "normal"> }
  [ saving ] { <type "saving"> }
]

CurrencyChoice[
  [ dollar dollars usd ] { <currency "usd"> }
  [ euro eur ]   { <currency "eur"> }
 ]

CardChoice[
  [ with yes ] { <card "with"> }
  [ without no ]  { <card "without"> }
]
